import * as React from 'react';
import { useEffect, useState } from 'react';
import { View } from 'react-native';
import { Button, Header, Icon, Input, Text } from 'react-native-elements';



function AltContato({route }) {

    const { usuario }= route.a
    const [nome, setNome] = useState();
    const [telefone , setTelefone] = useState();
    const [email, setEmail] = useState();
    useEffect(() => {
        if (route.params) {
            const { nome } = route.params
            setNome(nome)
            const { telefone } = route.params
            setTelefone(telefone)
            const { email } = route.params
            setEmail(email)
        }
    }, [])

    function alterarDados(){

        axios.put('http://localhost:3000/contato/'+getId
        ,
        
        {
        nome: getNome,
        telefone: getTelefone,
        email: getEmail,
        avatar_url: "https://e7.pngegg.com/pngimages/799/987/png-clipart-computer-icons-avatar-icon-design-avatar-heroes-computer-wallpaper-thumbnail.png"
        }).then(function (response) {
        alert ("Contato alterado com sucesso")
        }).catch(function (error) {
        console.log(error);
        
        });
        
        }

        function excluirDados(){

            axios.delete('http://localhost:3000/contato/'+getId)
            
            .then(function (response) {
            alert ("Contato excluido com sucesso");
            }).catch(function (error) {
            console.log(error);
            
            });
            
            }


    return (
        <View style={{ flex: 1 }}>

            <Header
                placement="center"
                leftComponent={
                    <Icon
                        raised
                        name='chevron-back-circle'
                        type='ionicon'
                        color='#1E90FF'
                        onPress={() => navigation.navigate('Contatos')} />}
                centerComponent={{ text: 'Contato', style: { fontSize: 50, color: '#fff' } }}

            />

            <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

                <Text>nome</Text>
                <Input
                    value= {nome}
                    containerStyle={{
                        marginVertical: 15,
                    }} />
                <Text>email</Text>
                <Input
                    value={email}
                    containerStyle={{
                        marginVertical: 15,
                    }} />
                <Text>Telefone</Text>
                <Input
                    value={telefone}
                    containerStyle={{
                        marginVertical: 15,
                    }} />

            </View>


            <Button
                title="Alterar"
                onPress={() => alterarDados() }
                //onPress={() => navigation.navigate('Login')}
                buttonStyle={{
                    backgroundColor: '#1E90FF',
                    borderRadius: 5,
                }}
                titleStyle={{ fontWeight: 'bold', fontSize: 20 }}
                containerStyle={{
                    marginHorizontal: 75,
                    height: 50,
                    marginVertical: 15,
                }}



            />
            <Button
                title="Excluir"
                onPress={() => excluirDados() }
                //onPress={() => navigation.navigate('CadUsuario')}
                buttonStyle={{
                    backgroundColor: '#FF0000',
                    borderRadius: 5,
                }}
                titleStyle={{ fontWeight: 'bold', fontSize: 20 }}
                containerStyle={{
                    marginHorizontal: 75,
                    height: 50,
                    marginVertical: 10,
                }}

            />

        </View>
    );
}





export default AltContato;
